mapper.select:
SELECT EXISTS (
    SELECT 1
    FROM GROUP_USER
    WHERE group_id = #{groupId} and user_id = #{userId} and group_state = 1
);

SELECT group_state
FROM GROUP_USER
WHERE user_id = #{userId} and group_id = #{groupId};

SELECT user_id
FROM GROUP_USER
WHERE group_id = #{groupId} and group_state = 1;
