mapper.select:
SELECT S.ID, NULL AS groupId, S.TITLE, S.LOCATION, S.LATITUDE, S.LONGITUDE, DATE_FORMAT(S.START_DATETIME, '%H:%i') AS startTime, DATE_FORMAT(S.END_DATETIME, '%H:%i') AS endTime, S.COLOR FROM SCHEDULE S WHERE S.USER_ID = #{userId} AND S.START_DATETIME >= #{startDateTime} AND S.END_DATETIME <= #{endDateTime}
UNION
SELECT GS.ID, GS.GROUP_ID AS groupId, GS.TITLE, GS.LOCATION, GS.LATITUDE, GS.LONGITUDE, DATE_FORMAT(GS.START_DATETIME, '%H:%i') AS startTime, DATE_FORMAT(GS.END_DATETIME, '%H:%i') AS endTime, GS.COLOR FROM GROUP_SCHEDULE GS JOIN GROUP_SCHEDULE_PARTICIPANT GSP ON GS.ID = GSP.GROUP_SCHEDULE_ID WHERE GSP.USER_ID = #{userId} AND GS.START_DATETIME >= #{startDateTime} AND GS.END_DATETIME <= #{endDateTime} ORDER BY startTime
